$.fn.rotator=function(e){var t=$.extend({delay:5e3,fadeSpeed:"slow"},e);return this.each(function(){var e=this,n=$(this),a=$(n).children();$(a).hide().eq(Math.floor(Math.random()*$(a).length)).show();this.rotateElements=function(){$(n).children(":visible").delay(t.delay).fadeOut(t.fadeSpeed,function(){$(this).appendTo(n);$(n).children(":first").fadeIn(t.fadeSpeed,e.rotateElements)})};e.rotateElements()})};